import React, { useState } from 'react';
import { motion } from 'framer-motion';
import { useParams, Link } from 'react-router-dom';
import { ArrowLeft, Heart, Share, MapPin, Home, Calendar, User, Star } from 'lucide-react';
import { properties } from '../data/properties';
import { formatPrice, formatSurface, formatDate, getPropertyTypeIcon } from '../utils/formatters';
import ContactForm from '../components/ContactForm';

const PropertyDetailPage = () => {
  const { id } = useParams();
  const property = properties.find(p => p.id === id);
  const [currentImageIndex, setCurrentImageIndex] = useState(0);
  const [showContactForm, setShowContactForm] = useState(false);

  if (!property) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="text-center">
          <h2 className="text-2xl font-bold text-gray-900 mb-4">
            Propiedad no encontrada
          </h2>
          <Link to="/propiedades">
            <button className="px-6 py-3 bg-blue-600 text-white rounded-xl font-medium hover:bg-blue-700 transition-colors">
              Ver todas las propiedades
            </button>
          </Link>
        </div>
      </div>
    );
  }

  const handleContactSubmit = (formData) => {
    console.log('Solicitud de contacto:', formData);
    alert('Â¡Gracias! Nos pondremos en contacto contigo pronto.');
    setShowContactForm(false);
  };

  return (
    <div className="min-h-screen bg-gray-50">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        <motion.div
          className="flex items-center gap-4 mb-6"
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.5 }}
        >
          <Link to="/propiedades">
            <motion.button
              className="p-2 rounded-xl bg-white shadow-sm border border-gray-200 hover:bg-gray-50 transition-colors"
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
            >
              <ArrowLeft className="w-5 h-5 text-gray-600" />
            </motion.button>
          </Link>
          <div>
            <h1 className="text-2xl md:text-3xl font-bold text-gray-900">
              {property.title}
            </h1>
            <div className="flex items-center gap-2 text-gray-600 mt-1">
              <MapPin className="w-4 h-4" />
              <span>{property.location}</span>
            </div>
          </div>
        </motion.div>

        <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
          <div className="lg:col-span-2 space-y-8">
            <motion.div
              className="bg-white rounded-2xl shadow-lg overflow-hidden"
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.5, delay: 0.1 }}
            >
              <div className="relative">
                <img
                  src={property.images[currentImageIndex]}
                  alt={property.title}
                  className="w-full h-96 object-cover"
                />

                <div className="absolute top-4 left-4 flex gap-2">
                  <span className={`px-3 py-1 rounded-full text-sm font-semibold text-white ${
                    property.operation === 'venta' ? 'bg-green-500' : 'bg-blue-500'
                  }`}>
                    {property.operation === 'venta' ? 'Venta' : 'Renta'}
                  </span>
                  {property.featured && (
                    <span className="px-3 py-1 rounded-full text-sm font-semibold bg-yellow-500 text-white flex items-center gap-1">
                      <Star className="w-3 h-3" />
                      Destacada
                    </span>
                  )}
                </div>

                <div className="absolute top-4 right-4 flex gap-2">
                  <motion.button
                    className="p-2 rounded-full bg-white/90 text-gray-600 hover:text-red-500 transition-colors"
                    whileHover={{ scale: 1.1 }}
                    whileTap={{ scale: 0.9 }}
                  >
                    <Heart className="w-5 h-5" />
                  </motion.button>
                  <motion.button
                    className="p-2 rounded-full bg-white/90 text-gray-600 hover:text-blue-500 transition-colors"
                    whileHover={{ scale: 1.1 }}
                    whileTap={{ scale: 0.9 }}
                  >
                    <Share className="w-5 h-5" />
                  </motion.button>
                </div>

                {property.images.length > 1 && (
                  <div className="absolute bottom-4 left-1/2 transform -translate-x-1/2 flex gap-2">
                    {property.images.map((_, index) => (
                      <button
                        key={index}
                        onClick={() => setCurrentImageIndex(index)}
                        className={`w-3 h-3 rounded-full transition-colors ${
                          index === currentImageIndex ? 'bg-white' : 'bg-white/50'
                        }`}
                      />
                    ))}
                  </div>
                )}
              </div>

              {property.images.length > 1 && (
                <div className="p-4 border-t border-gray-200">
                  <div className="flex gap-2 overflow-x-auto">
                    {property.images.map((image, index) => (
                      <button
                        key={index}
                        onClick={() => setCurrentImageIndex(index)}
                        className={`flex-shrink-0 w-20 h-20 rounded-lg overflow-hidden border-2 transition-colors ${
                          index === currentImageIndex ? 'border-blue-500' : 'border-gray-200'
                        }`}
                      >
                        <img
                          src={image}
                          alt={`Vista ${index + 1}`}
                          className="w-full h-full object-cover"
                        />
                      </button>
                    ))}
                  </div>
                </div>
              )}
            </motion.div>

            <motion.div
              className="bg-white rounded-2xl shadow-lg p-6"
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.5, delay: 0.2 }}
            >
              <div className="flex items-center justify-between mb-6">
                <div className="text-3xl font-bold text-blue-600">
                  {formatPrice(property.price, property.operation)}
                </div>
                <div className="flex items-center gap-2 text-gray-600">
                  <User className="w-4 h-4" />
                  <span>{property.views} vistas</span>
                </div>
              </div>

              <div className="grid grid-cols-2 md:grid-cols-4 gap-6 mb-6">
                <div className="text-center">
                  <div className="text-2xl mb-1">{getPropertyTypeIcon(property.type)}</div>
                  <div className="text-sm text-gray-500">Tipo</div>
                  <div className="font-semibold capitalize">{property.type}</div>
                </div>
                <div className="text-center">
                  <div className="text-2xl mb-1">ð</div>
                  <div className="text-sm text-gray-500">Superficie</div>
                  <div className="font-semibold">{formatSurface(property.surface)}</div>
                </div>
                {property.bedrooms > 0 && (
                  <div className="text-center">
                    <div className="text-2xl mb-1">ðï¸</div>
                    <div className="text-sm text-gray-500">RecÃ¡maras</div>
                    <div className="font-semibold">{property.bedrooms}</div>
                  </div>
                )}
                <div className="text-center">
                  <div className="text-2xl mb-1">ð¿</div>
                  <div className="text-sm text-gray-500">BaÃ±os</div>
                  <div className="font-semibold">{property.bathrooms}</div>
                </div>
              </div>

              <div className="border-t border-gray-200 pt-6">
                <h3 className="text-lg font-semibold text-gray-900 mb-3">
                  DescripciÃ³n
                </h3>
                <p className="text-gray-600 leading-relaxed">
                  {property.description}
                </p>
              </div>

              <div className="border-t border-gray-200 pt-6 mt-6">
                <div className="flex items-center gap-2 text-sm text-gray-500">
                  <Calendar className="w-4 h-4" />
                  <span>Publicado el {formatDate(property.createdAt)}</span>
                </div>
              </div>
            </motion.div>
          </div>

          <div className="space-y-6">
            <motion.div
              initial={{ opacity: 0, x: 20 }}
              animate={{ opacity: 1, x: 0 }}
              transition={{ duration: 0.5, delay: 0.3 }}
            >
              <ContactForm
                propertyTitle={property.title}
                onSubmit={handleContactSubmit}
              />
            </motion.div>

            <motion.div
              className="bg-white rounded-2xl shadow-lg p-6"
              initial={{ opacity: 0, x: 20 }}
              animate={{ opacity: 1, x: 0 }}
              transition={{ duration: 0.5, delay: 0.4 }}
            >
              <h3 className="text-lg font-semibold text-gray-900 mb-4">
                InformaciÃ³n Adicional
              </h3>
              <div className="space-y-3">
                <div className="flex justify-between">
                  <span className="text-gray-600">ID de Propiedad:</span>
                  <span className="font-medium">#{property.id}</span>
                </div>
                <div className="flex justify-between">
                  <span className="text-gray-600">Estacionamientos:</span>
                  <span className="font-medium">{property.parking || 'No incluye'}</span>
                </div>
                <div className="flex justify-between">
                  <span className="text-gray-600">Estado:</span>
                  <span className="font-medium text-green-600">Disponible</span>
                </div>
              </div>
            </motion.div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default PropertyDetailPage;
